import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const EventForm = () => {
  const [event, setEvent] = useState({
    title: '',
    description: '',
    date: '',
    location: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    if (id) {
      const fetchEvent = async () => {
        try {
          const res = await axios.get(`http://localhost:5000/api/events/${id}`);
          setEvent({
            ...res.data,
            date: new Date(res.data.date).toISOString().split('T')[0]
          });
        } catch (err) {
          console.error(err);
        }
      };
      fetchEvent();
    }
  }, [id]);

  const handleChange = (e) => {
    setEvent({ ...event, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    try {
      if (id) {
        await axios.put(`http://localhost:5000/api/events/${id}`, event);
      } else {
        await axios.post('http://localhost:5000/api/events', event);
      }
      navigate('/', { state: { message: id ? 'Event updated successfully!' : 'Event created successfully!' } });
    } catch (err) {
      console.error(err);
      setIsSubmitting(false);
    }
  };

  return (
    <div className="container">
      <h2 className="text-center">{id ? 'Edit Event' : 'Create New Event'}</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Event Title</label>
          <input
            type="text"
            name="title"
            value={event.title}
            onChange={handleChange}
            required
            placeholder="Enter event title"
          />
        </div>
        <div>
          <label>Description</label>
          <textarea
            name="description"
            value={event.description}
            onChange={handleChange}
            required
            placeholder="Describe your event"
          />
        </div>
        <div>
          <label>Date</label>
          <input
            type="date"
            name="date"
            value={event.date}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Location</label>
          <input
            type="text"
            name="location"
            value={event.location}
            onChange={handleChange}
            required
            placeholder="Where is the event?"
          />
        </div>
        <div className="mt-20">
          <button 
            type="submit" 
            className="btn-primary"
            disabled={isSubmitting}
          >
            {isSubmitting ? 'Saving...' : 'Save Event'}
            {isSubmitting && <span className="loading" style={{ width: '20px', height: '20px', marginLeft: '10px' }}></span>}
          </button>
        </div>
      </form>
    </div>
  );
};

export default EventForm;
