import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useLocation } from 'react-router-dom';

const EventList = () => {
  const [events, setEvents] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const location = useLocation();

  useEffect(() => {
    fetchEvents();
    
    // Check for success message from navigation state
    if (location.state?.message) {
      alert(location.state.message);
      // Clear the state
      window.history.replaceState({}, document.title);
    }
  }, [location.state]);

  const fetchEvents = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/events');
      setEvents(res.data);
      setIsLoading(false);
    } catch (err) {
      console.error(err);
      setIsLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this event?')) {
      try {
        await axios.delete(`http://localhost:5000/api/events/${id}`);
        fetchEvents();
      } catch (err) {
        console.error(err);
      }
    }
  };

  if (isLoading) {
    return (
      <div className="container">
        <div className="loading"></div>
      </div>
    );
  }

  return (
    <div className="container">
      <header>
        <h1>Event Management</h1>
        <Link to="/add" className="btn-primary floating">
          <span>Add New Event</span>
          <span>📅</span>
        </Link>
      </header>

      {events.length === 0 ? (
        <div className="text-center">
          <h3>No events found</h3>
          <p>Create your first event by clicking the button above</p>
        </div>
      ) : (
        <ul>
          {events.map(event => (
            <li key={event._id}>
              <h3>{event.title}</h3>
              <p>{event.description}</p>
              <p><strong>📅 Date:</strong> {new Date(event.date).toLocaleDateString('en-US', { 
                year: 'numeric', 
                month: 'long', 
                day: 'numeric' 
              })}</p>
              <p><strong>📍 Location:</strong> {event.location}</p>
              <div style={{ display: 'flex', gap: '10px', marginTop: '15px' }}>
                <Link to={`/edit/${event._id}`} className="btn-primary">
                  Edit
                </Link>
                <button 
                  onClick={() => handleDelete(event._id)} 
                  className="btn-danger"
                >
                  Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default EventList;
